package org.usfirst.frc2337.RobotProject.commands;

import edu.wpi.first.wpilibj.command.Command;

import org.usfirst.frc2337.RobotProject.Robot;
import org.usfirst.frc2337.RobotProject.RobotMap;

/**
 * Drive for a specified time. May or may not be in a straight line. Linearly ramps up the speed.
 * 
 * @param seconds The time, in seconds, to drive for.
 */
public class AutonDriveForTimeGyroIterative extends Command {
	private double seconds;
	private double gyroValue;
	private double kP = .08;
	private double moveDelta = 0.02;	//how much to increase each iteration
	private double move = 0;
	
    public AutonDriveForTimeGyroIterative(double seconds) {
    	this.seconds = seconds;    	
    	
        // Use requires() here to declare subsystem dependencies
        // eg. requires(chassis);

        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        requires(Robot.chassis);

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
    }

    // Called just before this Command runs the first time
    protected void initialize() {
    	setTimeout(seconds);
    	Robot.chassis.disable();
    }

    // Called repeatedly when this Command is scheduled to run
    protected void execute() {
    	gyroValue = RobotMap.imu.getYaw();
    	
    	gyroValue = gyroValue * -kP;
    	
    	if (gyroValue > 0.5) {
    		gyroValue = .5;
    	}
    	if (gyroValue < -0.5) {
    		gyroValue = -0.5;
    	}
    	
    	move = move + moveDelta;
    	
    	if (move > 1) {
    		move = 1;
    	}
    	
    	Robot.chassis.driveMecanum(move, 0, gyroValue);
    }

    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        return isTimedOut();
    }

    // Called once after isFinished returns true
    protected void end() {
    	Robot.chassis.stopMotors();
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
    	Robot.chassis.stopMotors();
    }
}
